'use client';

import {
  BoldPlugin,
  CodePlugin,
  HighlightPlugin,
  ItalicPlugin,
  KbdPlugin,
  StrikethroughPlugin,
  SubscriptPlugin,
  SuperscriptPlugin,
  UnderlinePlugin,
} from '@udecode/plate-basic-marks/react';

import { CodeLeaf } from '@/registry/ui/code-node';
import { HighlightLeaf } from '@/registry/ui/highlight-node';
import { KbdLeaf } from '@/registry/ui/kbd-node';

export const BasicMarksKit = [
  BoldPlugin,
  CodePlugin.withComponent(CodeLeaf),
  ItalicPlugin,
  StrikethroughPlugin,
  UnderlinePlugin,
  SubscriptPlugin,
  SuperscriptPlugin,
  KbdPlugin.withComponent(KbdLeaf),
  HighlightPlugin.withComponent(HighlightLeaf),
];
