'use client';

import React from 'react';

import { cn, withRef } from '@udecode/cn';
import { PlateLeaf } from '@udecode/plate/react';

export const CodeSyntaxLeaf = withRef<typeof PlateLeaf>(
  ({ children, className, ...props }, ref) => {
    const tokenClassName = props.leaf.className as string;

    return (
      <PlateLeaf ref={ref} {...props} className={cn(tokenClassName, className)}>
        {children}
      </PlateLeaf>
    );
  }
);

import React from 'react';

import type { SlateLeafProps } from '@udecode/plate';

import { cn } from '@udecode/cn';
import { SlateLeaf } from '@udecode/plate';

export function CodeSyntaxLeafStatic({
  children,
  className,
  ...props
}: SlateLeafProps) {
  const tokenClassName = props.leaf.className as string;

  return (
    <SlateLeaf className={cn(tokenClassName, className)} {...props}>
      {children}
    </SlateLeaf>
  );
}
