'use client';

import { Plate, usePlateEditor } from '@udecode/plate/react';

import { Editor, EditorContainer } from '@/registry/ui/editor'; // Adjust path if your shadcn/ui components are elsewhere

export default function NextJsBasicEditorSetupDemo() {
  const editor = usePlateEditor();

  return (
    <div className="m-8 max-w-4xl rounded-lg border p-4 shadow">
      <Plate editor={editor}>
        <EditorContainer>
          <Editor placeholder="Type your amazing content here..." />
        </EditorContainer>
      </Plate>
    </div>
  );
}
