'use client';

import * as React from 'react';

import type { Value } from '@udecode/plate';

import {
  BasicMarksPlugin,
  BoldPlugin,
  ItalicPlugin,
  UnderlinePlugin,
} from '@udecode/plate-basic-marks/react';
import { ParagraphPlugin, Plate, usePlateEditor } from '@udecode/plate/react';

import { Editor, EditorContainer } from '@/registry/ui/editor';
import { FixedToolbar } from '@/registry/ui/fixed-toolbar';
import { MarkToolbarButton } from '@/registry/ui/mark-toolbar-button';
// import { Bold, Italic, Underline } from 'lucide-react'; // Example icons

const initialValue: Value = [
  {
    children: [
      { text: 'Hello! Try out the ' },
      { bold: true, text: 'bold' },
      { text: ', ' },
      { italic: true, text: 'italic' },
      { text: ', and ' },
      { text: 'underline', underline: true },
      { text: ' formatting.' },
    ],
    type: 'p',
  },
];

export default function NextJsEditorBasicMarksDemo() {
  const [editorValue, setEditorValue] = React.useState<Value>(initialValue);

  React.useEffect(() => {
    const savedValue = localStorage.getItem('nextjs-plate-marks-demo');
    if (savedValue) {
      try {
        setEditorValue(JSON.parse(savedValue));
      } catch (error) {
        console.error('Failed to parse saved editor content:', error);
        localStorage.setItem(
          'nextjs-plate-marks-demo',
          JSON.stringify(initialValue)
        );
      }
    }
  }, []);

  const editor = usePlateEditor({
    plugins: [ParagraphPlugin, BasicMarksPlugin],
    value: editorValue,
  });

  return (
    <div className="m-8 max-w-4xl rounded-lg border p-4 shadow">
      <Plate
        onChange={({ value }) => {
          setEditorValue(value);
          localStorage.setItem(
            'nextjs-plate-marks-demo',
            JSON.stringify(value)
          );
        }}
        editor={editor}
      >
        <FixedToolbar>
          <MarkToolbarButton nodeType={BoldPlugin.key} tooltip="Bold (⌘+B)">
            {/* <Bold className="size-4" /> */}B
          </MarkToolbarButton>
          <MarkToolbarButton nodeType={ItalicPlugin.key} tooltip="Italic (⌘+I)">
            {/* <Italic className="size-4" /> */}I
          </MarkToolbarButton>
          <MarkToolbarButton
            nodeType={UnderlinePlugin.key}
            tooltip="Underline (⌘+U)"
          >
            {/* <Underline className="size-4" /> */}U
          </MarkToolbarButton>
        </FixedToolbar>
        <EditorContainer className="mt-2">
          <Editor placeholder="Type your amazing content here..." />
        </EditorContainer>
      </Plate>
    </div>
  );
}
